import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectProperty,
  setTextArrayField,
  setProperty,
  removeOption,
} from "../../features/questionProperties/questionSlice";
import {
  TextField,
  InputAdornment,
  IconButton,
  Button,
  Box,
  Menu,
  MenuItem,
  makeStyles,
  FormControl,
  InputLabel,
  FilledInput,
  Paper,
} from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import DeleteIcon from "@material-ui/icons/Delete";

const useStyles = makeStyles((theme) => ({
  boxy: {
    borderRadius: 0,
  },
  paper: {
    height: "250px",
    overflow: "auto",
    wordWrap: "break-word",
    wordBreak: "break-word",
  }
}));

export const TextArrayProperty = ({ name, propertyName }) => {
  const property = useSelector(selectProperty(propertyName));
  const dispatch = useDispatch();

  const handleAddOptionClick = () => {
    dispatch(
      setProperty({
        property: propertyName,
        value: [...property, ""],
      })
    );
  };

  const classes = useStyles();
  return (
    <>
    <Box style={{textAlign: "center"}}>
      <Button onClick={handleAddOptionClick}>add {name}</Button>
    </Box>
      <Paper elevation={0} variant="outlined" className={classes.paper}>
        {property.length > 0 && property.map((_, index) => (
          <EachOptioni index={index} name={name} propertyName={propertyName} />
        ))}
      </Paper>
    </>
  );
};

function EachOptioni({ index, name, propertyName }) {
  const option = useSelector((state) => state.question[propertyName][index]);
  const dispatch = useDispatch();

  const handleChange = (index, event) => {
    dispatch(
      setTextArrayField({
        property: propertyName,
        index: index,
        value: event.target.value,
      })
    );
  };

  const handleRemoveOptionClick = (index) => dispatch(removeOption(index));

  // MENU STUFF

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  // ----------

  const getInputProps = (index) => ({
    endAdornment: (
      <InputAdornment position="end">
        <IconButton size="small" onClick={handleMenuClick}>
          <MoreHorizIcon />
        </IconButton>
        <Menu
          id="simple-menu"
          keepMounted
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
        >
          {/* switch case without break for options */}
          <MenuItem button={false}>
            <FormControl fullWidth>
              <InputLabel variant="filled">Option tooltip</InputLabel>
              <FilledInput
                required
                className={classes.boxy}
                autoFocus
                autoComplete="off"
                type="text"
                id={`option-${index + 1}-tooltip`}
                placeholder="Enter option tooltip text here.."
                value={option || ""}
                onChange={e => handleChange(index, e)}
              />
            </FormControl>
          </MenuItem>
          <MenuItem onClick={handleMenuClose}>My account</MenuItem>
          <MenuItem onClick={handleMenuClose}>Logout</MenuItem>
        </Menu>
        <IconButton size="small" onClick={() => handleRemoveOptionClick(index)}>
          <DeleteIcon />
        </IconButton>
      </InputAdornment>
    ),
  });

  const classes = useStyles();
  return (
    <TextField
      label={`Option ${index}`}
      variant="filled"
      required
      key={index}
      fullWidth
      type="text"
      placeholder={name}
      value={option || ""}
      onChange={(e) => handleChange(index, e)}
      InputProps={getInputProps(index)}
    />
  );
}
