import React, { createContext, useReducer } from "react";
import { questionnaireReducer } from "../reducers/questionnaireReducer";
import { useEffect } from "react";

export const QuestionnaireContext = createContext();

const timelineReducer = (state, action) => {
  switch (action.type) {
    case "UPDATED":
      return action.timeline;
    default:
      return state;
  }
};

const QuestionnaireContextProvider = props => {
  const [timeline, dispatchTimeline] = useReducer(timelineReducer, {
    past: [],
    present: [
      {
        id: "v1",
        type: "radio",
        title: "untitled radio",
        options: ["option 1", "option 2", "option 3", "option 4"]
      },
      {
        id: "v2",
        type: "checkbox",
        title: "untitled checkbox",
        options: ["option 1", "option 2", "option 3", "option 4"]
      },
      {
        id: "v3",
        type: "range",
        title: "untitled range",
        options: ["option 1", "option 2", "option 3", "option 4"]
      },
      {
        id: "v4",
        type: "likert",
        title: "untitled likert",
        options: ["option 1", "option 2", "option 3", "option 4"]
      }
    ],
    future: []
  });
  const [questions, dispatch] = useReducer(questionnaireReducer, timeline.present);
  let count=0;
  useEffect(() => {
    if (count!=0) {
      const t = {past: [...timeline.past, timeline.present], present: questions, future: [...timeline.future]};
      dispatchTimeline({ type: "UPDATED", timeline:  t});
      console.log(t);
    }
    console.log(timeline);
    count++;
  }, [questions]);
  return (
    <QuestionnaireContext.Provider
      value={{ questions, dispatch, timeline, dispatchTimeline }}
    >
      {props.children}
    </QuestionnaireContext.Provider>
  );
};

export default QuestionnaireContextProvider;
