import React, { useContext } from "react";
import { v1 as uuidv1 } from "uuid";
import {
  makeStyles,
  Box,
  Container,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Typography,
  Grid,
  Paper,
  AppBar,
  Toolbar
} from "@material-ui/core";
import { QuestionnaireContext } from "../contexts/QuestionnaireContext";
import Question from "./Question";
import ToggleGridAreasButton from "./buttons/ToggleGridAreasButton";
import AddQuestionButton2 from "./buttons/AddQuestionButton2";
import StringifiedJSONCard from "./StringifiedJSONCard";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import { QUESTION_TYPES } from "./QuestionTypes";

import "./index.css";
import { SettingsContext } from "../contexts/SettingsContext";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
}));

const QuestionsPage = () => {
  const { settings } = useContext(SettingsContext);
  const { questions, dispatch } = useContext(QuestionnaireContext);
  const onDragEnd = React.useCallback(result => {
    const { source, destination } = result;

    if (!destination) {
      return;
    }

    switch (source.droppableId) {
      case "BAG":
        dispatch({ type: "REORDER", source: source, destination: destination });
        break;
      case "SHOP":
        dispatch({ type: "CLONE", source: source, destination: destination });
        break;
      default:
        break;
    }
  }, []);
  const classes = useStyles();

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <AppBar color="grey" position="fixed" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" noWrap>
            Clipped drawer
          </Typography>
        </Toolbar>
      </AppBar>
      <Sidebar items={QUESTION_TYPES} />
      {/* <Container maxWidth="lg">
      </Container> */}
      <Grid
        container
        direction="column"
        justify="center"
        alignItems="stretch"
        style={{
          background: settings.showGridAreas ? "lightgrey" : "transparent"
        }}
      >
        <Grid container direction="row" justify="center" alignItems="center">
          <Grid
            item
            xs={6}
            style={{
              background: settings.showGridAreas ? "lightblue" : "transparent"
            }}
          >
            <TopSection />
          </Grid>
          <Grid
            item
            xs={6}
            style={{
              background: settings.showGridAreas ? "lightgreen" : "transparent"
            }}
          >
            <BottomSection items={questions} />
          </Grid>
        </Grid>
      </Grid>
    </DragDropContext>
  );
};

export default QuestionsPage;

const TopSection = () => {
  return (
    <Container style={{ textAlign: "center", margin: "2em auto" }}>
      {/* <AddQuestionButton /> */}
      <ToggleGridAreasButton />
      <StringifiedJSONCard />
    </Container>
  );
};

const BottomSection = ({ items }) => {
  const { questions } = useContext(QuestionnaireContext);

  return (
    <Container style={{ textAlign: "center", margin: "1em auto" }}>
      <Typography variant="h4" style={{ margin: "1em 0" }}>
        Questions
      </Typography>
      <Droppable droppableId="BAG">
        {(provided, snapshot) => (
          <Box ref={provided.innerRef} className="shopping-bag">
            {questions.map((question, index) => (
              <Draggable
                key={question.id}
                draggableId={question.id}
                index={index}
              >
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={provided.draggableProps.style}
                  >
                    <Question key={uuidv1()} question={question} />
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </Box>
        )}
      </Droppable>
      {/* <AddQuestionButton2 /> */}
    </Container>
  );
};

// This method is needed for rendering clones of draggables
const getRenderItem = (items, className) => (provided, snapshot, rubric) => {
  const item = items[rubric.source.index];
  return (
    <Paper
      className={snapshot.isDragging ? "dragging1" : "not-dragging1"}
      {...provided.draggableProps}
      {...provided.dragHandleProps}
      ref={provided.innerRef}
      style={provided.draggableProps.style}
      // style={{ padding: "1em", color: "white" }}
    >
      <ListItem>
        <ListItemIcon style={{ color: "white" }}>{item.icon}</ListItemIcon>
        <ListItemText>
          <Typography variant="body1" style={{ color: "white" }}>
            {item.label}
          </Typography>
        </ListItemText>
      </ListItem>
    </Paper>
  );
};

const Sidebar = ({ items }) => {
  return (
    <Droppable
      renderClone={getRenderItem(items, "")}
      droppableId="SHOP"
      isDropDisabled={true}
    >
      {(provided, snapshot) => (
        <Drawer
          ref={provided.innerRef}
          style={{ width: 50 }}
          variant="persistent"
          anchor="right"
          open
        >
          <List>
            {items.map((item, index) => {
              const shouldRenderClone =
                item.id === snapshot.draggingFromThisWith;
              return (
                <React.Fragment style={{ textAlign: "left" }} key={item.id}>
                  {shouldRenderClone ? (
                    <ListItem button key={item.label}>
                      <ListItemIcon>{item.icon}</ListItemIcon>
                      <ListItemText
                        primary={item.label}
                        style={{ textAlign: "left" }}
                      />
                    </ListItem>
                  ) : (
                    <Draggable draggableId={item.id} index={index}>
                      {(provided, snapshot) => (
                        <div
                          key={item.label}
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <ListItem
                            button
                            className={snapshot.isDragging ? "dragging" : ""}
                          >
                            <ListItemIcon>{item.icon}</ListItemIcon>
                            <ListItemText primary={item.label} />
                          </ListItem>
                        </div>
                      )}
                    </Draggable>
                  )}
                </React.Fragment>
              );
            })}
            {provided.placeholder}
          </List>
        </Drawer>
      )}
    </Droppable>
  );
};
