import { createSlice } from "@reduxjs/toolkit";
import { initial2, preprocessQuestion, CLEAN_SUPER_QUESTION } from "../../utils";

export const questionSlice = createSlice({
  name: "question",
  initialState: CLEAN_SUPER_QUESTION,
  reducers: {
    setProperty: (state, action) => {
      state[action.payload.property] = action.payload.value;
    },
    resetAll: (state, action) => {
      return CLEAN_SUPER_QUESTION;
    }
  },
});

export const { setProperty, resetAll } = questionSlice.actions;

export const selectProperty = state = propertyName => state.question[propertyName];

export default questionSlice.reducer;
