import React from 'react'
import { useContext } from 'react';
import { QuestionnaireContext } from '../contexts/QuestionnaireContext';
import { Card, Typography } from '@material-ui/core';
import { useEffect } from 'react';

const StringifiedJSONCard = () => {
  const { questions } = useContext(QuestionnaireContext);
  // formats the ids
  const toPrint = questions.map((question, index) => {
    let obj = {
            ...question,
            id: `v${index + 1}`,
            labels: question.options
          };
          obj.type !== "range" ? delete obj.labels : delete obj.options;
    return obj;
  });
  return (
    <Card
      variant="outlined"
      style={{
        wordWrap: "break-word",
        wordBreak: "break-word",
        margin: "0.5em auto",
        padding: "2em",
        height: "150px",
        overflow: "scroll"
      }}
    >
      <Typography variant="body2">{JSON.stringify(toPrint)}</Typography>
    </Card>
  );
};

export default StringifiedJSONCard;
