import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectProperty,
  setProperty,
} from "../../features/questionProperties/questionSlice";
import {
  TextField,
  FormControl,
  InputLabel,
  FilledInput,
  makeStyles,
  Paper,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  boxy: {
    borderRadius: 0,
  },
  paper: {
    overflow: "auto",
    wordWrap: "break-word",
    wordBreak: "break-word",
  },
}));

export const TextProperty = ({ name, propertyName, ...props }) => {
  const property = useSelector(selectProperty(propertyName));
  const dispatch = useDispatch();

  const handleChange = (event) => {
    dispatch(
      setProperty({
        property: propertyName,
        value: event.target.value,
      })
    );
  };

  const classes = useStyles();

  return (
    // <TextField
    //   key={propertyName}
    //   fullWidth
    //   variant="filled"
    //   type="text"
    //   id={"outlined-basic"}
    //   value={property}
    //   onChange={handleChange}
    //   label={name}
    //   {...props}
    // />
    <Paper className={classes.paper} variant="outlined">
      <FormControl fullWidth>
        <InputLabel variant="filled">{name}</InputLabel>
        <FilledInput
        required
          className={classes.boxy}
          required
          autoFocus
          autoComplete="off"
          type="text"
          id={"outlined-basic"}
          placeholder={name}
          value={property || ""}
          onChange={handleChange}
          {...props}
        />
      </FormControl>
    </Paper>
  );
};
