import React, { useContext, useEffect } from "react";
import {
  Button,
  Dialog,
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
  Typography,
  Paper,
  Box,
  makeStyles,
  Divider,
} from "@material-ui/core";
import { QuestionnaireContext } from "../contexts/QuestionnaireContext";
import { SettingsContext } from "../contexts/SettingsContext";
import { NewQuestionContext } from "../contexts/NewQuestionContext";
import {
  PROPERTIES_BY_QUESTION_TYPE,
  SPECIAL_CONVERSION_CASES,
  CLEAN_SUPER_QUESTION,
} from "../utils";
import Card from "@material-ui/core/Card";
import { IOptions as classes } from "glob";
import CardContent from "@material-ui/core/CardContent";
import { borderColor } from "@material-ui/system";
import { WidthProperty } from "./QuestionnaireProperties";
import { useDispatch, useSelector } from "react-redux";
import {
  setQuestion,
  resetAll,
} from "../features/questionProperties/questionSlice";
import { setQuestionAtIndex } from "../features/questions/questionsSlice";
import { useState } from "react";
import TypeProperty from "./properties/TypeProperty";
import { createElement } from "react";
import store from "../app/store";
import { fade } from '@material-ui/core/styles/colorManipulator';


const EditDialog2 = ({ question, index, open, setOpen }) => {
  // const newQuestion = useSelector((state) => state.question);
  const dispatch2 = useDispatch();
  // const { dispatch } = useContext(QuestionnaireContext);

  // load question to editor state on dialog open
  useEffect(() => {
    dispatch2(setQuestion({ ...CLEAN_SUPER_QUESTION, ...question }));
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  // cancel all and return to QuestionsPage
  const handleClose = () => {
    // dispatch(resetAll());
    setOpen(false);
  };

  return (
    <Dialog open={open} onClose={handleClose} fullWidth maxWidth="md">
      {/* <form style={{ padding: "1em" }}> */}
      {/* <form onSubmit={handleSubmit} style={{ padding: "1em" }}> */}
      <Grid container direction="column" justify="center" alignItems="stretch">
        <Box p={2.5}>
          <DialogHeader index={index}/>
          <DialogBody />
        </Box>
        <DialogFooter
          id={question.id}
          handleSubmit={handleSubmit}
          handleClose={handleClose}
          setOpen={setOpen}
        />
      </Grid>
      {/* </form> */}
    </Dialog>
  );
};

export default EditDialog2;

const DialogHeader = ({ index }) => {
  return (
    <Grid container direction="row" justify="space-between" alignItems="center">
      <Grid item>
        <Typography variant="h5">
          {`Question ${index + 1}`}
        </Typography>
      </Grid>
      <Grid item>
        <TypeProperty />
      </Grid>
    </Grid>
  );
};

const useStyles = makeStyles((theme) => ({
  body: {
    "&>*": {
      marginTop: theme.spacing(2),
      marginBottom: theme.spacing(2),
      padding: theme.spacing(2),
    },
  },
  button1: {
    // background: fade(theme.palette.secondary.main, 0.1),
    height: "75px",
    width: "100%", 
    borderRadius: 0,
  },
  button2: {
    // background: fade(theme.palette.primary.main, 0.1),
    height: "75px",
    width: "100%", 
    borderRadius: 0,
  },
  sticky: {
    background: theme.palette.background.paper,
    position: "sticky",
    bottom: 0,
  },
}));

const DialogBody = () => {
  const type = useSelector((state) => state.question.type);
  const classes = useStyles();
  return (
    <div className={classes.body}>
      <Paper variant="outlined">
        <Typography variant="subtitle2">Required properties</Typography>
        {PROPERTIES_BY_QUESTION_TYPE[type.toUpperCase()][
          "requiredProperties"
        ].map((el) => (
          <Box p={1}>{createElement(el)}</Box>
        ))}
      </Paper>
      <Paper variant="outlined">
        <Typography variant="subtitle2">Optional properties</Typography>
        {PROPERTIES_BY_QUESTION_TYPE[type.toUpperCase()][
          "optionalProperties"
        ].map((el) => (
          <Box p={1}>{createElement(el)}</Box>
        ))}
      </Paper>
    </div>
  );
};

const DialogFooter = ({ id, handleSubmit, handleClose, setOpen }) => {
  // const newQuestion = useSelector((state) => state.question);
  const { dispatch } = useContext(QuestionnaireContext);
  // const dispatch2 = useDispatch();

  // dispatch action to questionnaireReducer to update question
  const handleClick = (event) => {
    // event.preventDefault();
    // do post processing here
    // dispatch(setQuestionAtIndex({ index: index, question: newQuestion }));
    const state = store.getState();
    const newQuestion = state.question;
    dispatch({ type: "UPDATE_QUESTION", id: id, new: newQuestion });
    console.log(newQuestion);
    setOpen(false);
    // dispatch(resetAll());
  };

  const classes = useStyles();

  return (
    <div className={classes.sticky}>
    <Divider />
    <Grid  container direction="row" justify="center" alignItems="center">
      <Grid item xs>
        <Button
          disableElevation
          className={classes.button1}
          // variant="contained"
          color="secondary"
          onClick={handleClose}
        >
          cancel
        </Button>
      </Grid>
      <Divider orientation="vertical" flexItem />
      <Grid item xs>
        <Button
          disableElevation
          className={classes.button2}
          onClick={handleSubmit && handleClick}
          // variant="contained"
          color="primary"
        >
          save
        </Button>
      </Grid>
    </Grid>
    </div>
  );
};
