import React, { createContext, useReducer } from 'react';
import { questionnaireReducer } from '../reducers/questionnaireReducer';
import { useEffect } from 'react';

export const QuestionnaireContext = createContext();

const timelineReducer = (state, action) => {
  switch(action.type) {
    case "ADD_TIMELINE":
      if (state.present == null) {
        return {past: state.past, present: action.questions, future: state.future};
      } else {
        return {past: [...state.past, state.present], present: action.questions, future: state.future};
      }
    case "UNDO":
      if (state.past.length > 0) {
        const newPast = state.past.slice(0, -1);
        const newFuture = [state.present, ...state.future];
        return {past: newPast, present: state.past[state.past.length - 1], future: newFuture};
      } else {
        return state;
      }
    case "REDO":
      if (state.future.length > 0) {
        const newPast = [...state.past, state.present];
        const newFuture = state.future.slice(1, state.future.length);
        return {past: newPast, present: state.future[0], future: newFuture};
      } else {
        return state;
      }
  default:
    return state;
  }
}

const QuestionnaireContextProvider = (props) => {
  const [questions, dispatch] = useReducer(questionnaireReducer, [
    {id: 'v1', type: "radio", title: "untitled radio", options: ["option 1", "option 2", "option 3", "option 4"]},
    // {id: 'v2', type: "checkbox", title: "untitled checkbox", options: ["option 1", "option 2", "option 3", "option 4"]},
    // {id: 'v3', type: "range", title: "untitled range", options: ["option 1", "option 2", "option 3", "option 4"]},
    // {id: 'v4', type: "likert", title: "untitled likert", options: ["option 1", "option 2", "option 3", "option 4"]},
  ]);
  const [timeline, dispatchTimeline] = useReducer(timelineReducer, {past: [], present: null, future: []});
  // useEffect(() => {
  //   dispatchTimeline({ type: "ADD_QUESTIONS", questions: questions })
  // }, []);
  useEffect(() => {
    dispatchTimeline({ type: "ADD_TIMELINE", questions: questions});
  }, [questions]);
  useEffect(() => {
    dispatch({ type: "SET_QUESTIONS", questions: timeline.present });
  }, [timeline.present]);
  return (
    <QuestionnaireContext.Provider value={{questions, dispatch, timeline, dispatchTimeline}}>
      {props.children}
    </QuestionnaireContext.Provider>
  );
}

export default QuestionnaireContextProvider;
