import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import { Box, Container } from '@material-ui/core';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles(theme => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: 200,
    },
    position: 'absolute', 
    left: '50%', 
    top: '50%',
    transform: 'translate(-50%, -50%)',
    // background: 'grey'
  },
  element: {
    margin: '10px'
  }
}));

export default function LoginForm() {
  const classes = useStyles();
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e.target.elements["username"].value);
  }
  return (
    <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit}>
      <div>
        <TextField className={classes.element} id="username" label="username" variant="outlined" />
      </div>
      <div>
        <TextField className={classes.element} id="password" label="password" variant="outlined" type="password" />
      </div>
      <div>
        <Button className={classes.element} variant="contained" color="primary" type="submit">
          submit
        </Button>
      </div>
    </form>
  );
}
