export const settingsReducer = (state, action) => {
  switch(action.type) {
    case 'TOGGLE_GRID_AREAS':
      return {...state, showGridAreas: !state.showGridAreas};
    case 'TOGGLE_EXPANSION_PANELS':
      return {...state, expanded: !state.expanded}
    default:
      return state;
  }
}
