import { createSlice } from "@reduxjs/toolkit";
import { initial2, preprocessQuestion } from "../../utils";

export const questionSlice = createSlice({
  name: "question",
  // initialState: {
  //   section_start: undefined,
  //   title: undefined,
  //   tooltip: undefined,
  // },
  // initialState: CLEAN_QUESTION,
  initialState: initial2,
  reducers: {
    setProperty: (state, action) => {
      state[action.payload.property] = action.payload.value;
    },
    setQuestionBeforeEdit: (state, action) => {
      return action.payload;
    },
    setType: (state, action) => {
      state.type = action.payload;
    },
    setSectionStart: (state, action) => {
      state.section_start = action.payload;
    },
    setTitle: (state, action) => {
      state.title = action.payload;
    },
    setTooltip: (state, action) => {
      state.tooltip = action.payload;
    },
    toggleHidden: (state) => {
      state.hidden = !state.hidden;
    },
    toggleRequired: (state) => {
      state.required = !state.required;
    },
    toggleSectionEnd: (state) => {
      state.section_end = !state.section_end;
    },
    removeOption: (state, action) => {
      state.options = state.options.filter(
        (_, index) => index !== action.payload
      );
    },
    addOption: (state) => {
      state.options = [...state.options, ""];
    },
    setOption: (state, action) => {
      state.options[action.payload.index] = action.payload.title;
    },
    setFullOption: (state, action) => {
      state.options[action.payload.index] = action.payload.object;
    },
    setOptions: (state, action) => {
      state.options = action.payload;
    },
    setShowOtherwise: (state, action) => {
      state.show_otherwise = action.payload;
    },
    setOtherwiseLabel: (state, action) => {
      state.otherwise_label = action.payload;
    },
    setOtherwiseTooltip: (state, action) => {
      state.otherwise_tooltip = action.payload;
    },
  },
});

export const {
  setProperty,
  setQuestionBeforeEdit,
  setType,
  setSectionStart,
  setTitle,
  setTooltip,
  toggleHidden,
  toggleRequired,
  toggleSectionEnd,
  removeOption,
  addOption,
  setOption,
  setFullOption,
  setOptions,
  setShowOtherwise,
  setOtherwiseLabel,
  setOtherwiseTooltip,
} = questionSlice.actions;

export const asyncSet = (question) => (dispatch) => {
  setTimeout(() => {
    dispatch(setQuestionBeforeEdit(preprocessQuestion(question)));
  }, 0);
};

export const selectQuestion = (state) => state.question;
export const selectType = (state) => state.question.type;
export const selectSectionStart = (state) => state.question.section_start;
export const selectTitle = (state) => state.question.title;
export const selectTooltip = (state) => state.question.tooltip;
export const selectHidden = (state) => state.question.hidden;
export const selectRequired = (state) => state.question.required;
export const selectSectionEnd = (state) => state.question.section_end;
export const selectOptions = (state) => state.question.options;
export const selectShowOtherwise = (state) => state.question.show_otherwise;
export const selectOtherwiseLabel = (state) => state.question.otherwise_label;
export const selectOtherwiseTooltip = (state) =>
  state.question.otherwise_tooltip;

export default questionSlice.reducer;
