import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectProperty,
  setTextArrayField,
  setTextArrayElement,
  setProperty,
  removeOption,
} from "../../features/questionProperties/questionSlice";
import {
  TextField,
  InputAdornment,
  IconButton,
  Button,
  Box,
  Menu,
  MenuItem,
  makeStyles,
  FormControl,
  InputLabel,
  FilledInput,
  Paper,
  Slide,
  Grid,
  Typography,
  Tooltip,
} from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import DeleteIcon from "@material-ui/icons/Delete";
import AddIcon from "@material-ui/icons/Add";
import { CLEAN_SUPER_OPTION } from "../../utils";

const useStyles = makeStyles((theme) => ({
  boxy: {
    borderRadius: 0,
  },
  paper: {
    height: "250px",
    overflow: "auto",
    wordWrap: "break-word",
    wordBreak: "break-word",
  },
  button: {
    marginTop: theme.spacing(2),
    height: "55px",
  },
  noneBox: {
    width: "100%",
    height: "100%",
  },
}));

export const TextArrayProperty = ({ name, propertyName }) => {
  const property = useSelector(selectProperty(propertyName));
  const dispatch = useDispatch();

  useEffect(() => {
    if (property === undefined) {
      dispatch(setProperty({ property: propertyName, value: [] }));
    }
  }, [property]);

  const handleAddOptionClick = () => {
    dispatch(
      setProperty({
        property: propertyName,
        value: [...property, ""],
      })
    );
  };

  const classes = useStyles();
  return (
    <>
      <Paper elevation={0} variant="outlined" className={classes.paper}>
        {property.length > 0 ? (
          property.map((_, index) => (
            <EachOptioni
              index={index}
              name={name}
              propertyName={propertyName}
            />
          ))
        ) : (
          <Grid
            className={classes.noneBox}
            container
            direction="row"
            justify="center"
            alignItems="center"
          >
            <Grid item>
              <Typography
                variant="body1"
                style={{ userSelect: "none" }}
              >{`no ${propertyName} provided`}</Typography>
            </Grid>
          </Grid>
        )}
      </Paper>
      <Box textAlign="center">
        <AddOptionButton onClick={handleAddOptionClick} />
      </Box>
    </>
  );
};

function EachOptioni({ index, name, propertyName }) {
  const type = useSelector(selectProperty("type"));
  const option = useSelector((state) => state.question[propertyName][index]);
  const dispatch = useDispatch();

  // makes sure the options are in object format (unless range type)
  useEffect(() => {
    if (type !== "range" && typeof option === "string") {
      dispatch(
        setTextArrayElement({
          property: propertyName,
          index: index,
          value: { ...CLEAN_SUPER_OPTION, title: option },
        })
      );
    }
  }, [option]);

  const handleChange = (index, event) => {
    if (type !== "range") {
      dispatch(
        setTextArrayElement({
          property: propertyName,
          index: index,
          value: { ...option, title: event.target.value },
        })
      );
    } else {
      dispatch(
        setTextArrayField({
          property: propertyName,
          index: index,
          value: event.target.value,
        })
      );
    }
  };

  const handleRemoveOptionClick = (index) =>
    dispatch(removeOption({ property: propertyName, index: index }));

  const classes = useStyles();
  return (
    // <Slide in direction="right">
    <FormControl fullWidth required>
      <InputLabel variant="filled" style={{ userSelect: "none" }}>
        {type === "range" ? `Label ${index + 1}` : `Option ${index + 1}`}
      </InputLabel>
      <FilledInput
        // required
        className={classes.boxy}
        autoFocus
        autoComplete="off"
        type="text"
        id={`option-${index + 1}`}
        placeholder={
          type === "range"
            ? "Enter range label here.."
            : "Enter option title here.."
        }
        value={(type === "range" ? option : option["title"]) || ""}
        onChange={(e) => handleChange(index, e)}
        endAdornment={
          <InputAdornment position="end">
            <EachOptionMenu index={index} />
            <IconButton
              size="small"
              onClick={() => handleRemoveOptionClick(index)}
            >
              <DeleteIcon />
            </IconButton>
          </InputAdornment>
        }
      />
    </FormControl>
    // </Slide>
  );
}

const AddOptionButton = (props) => {
  const type = useSelector(selectProperty("type"));
  const classes = useStyles();
  return (
    <Tooltip title={type === "range" ? "add a label" : "add an option"}>
      <Button
        disableElevation
        variant="contained"
        fullWidth
        color="primary"
        className={classes.button}
        {...props}
      >
        <AddIcon />
      </Button>
    </Tooltip>
  );
};

const EachOptionMenu = ({ index }) => {
  // MENU STUFF

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  // ----------

  return (
    <>
      <IconButton size="small" onClick={handleMenuClick}>
        <MoreHorizIcon />
      </IconButton>
      <Menu
        id="simple-menu"
        keepMounted
        anchorEl={anchorEl}
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
      >
        {/* switch case without break for options */}
        <MenuItem button={false}>
          <FormControl fullWidth>
            <InputLabel variant="filled">Option tooltip</InputLabel>
            <FilledInput
              required
              className={classes.boxy}
              autoFocus
              autoComplete="off"
              type="text"
              id={`option-${index + 1}-tooltip`}
              placeholder="Enter option tooltip text here.."
              value={option || ""}
              onChange={(e) => handleChange(index, e)}
            />
          </FormControl>
        </MenuItem>
        <MenuItem onClick={handleMenuClose}>My account</MenuItem>
        <MenuItem onClick={handleMenuClose}>Logout</MenuItem>
      </Menu>
    </>
  );
};
