import React, { useState } from 'react';
import { List, arrayMove } from 'react-movable';
import { Card, CardContent, FormControlLabel, Typography } from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import { Container } from '@material-ui/core';
import ButtonAppBar from './ButtonAppBar';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';

import CardActions from '@material-ui/core/CardActions';
// import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';

import ArrowBackIcon from '@material-ui/icons/ArrowBack';

import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';

const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(3),
  },
  caption: {
    position: 'absolute',
    top: 0,
    left: 0
  },
  con: {
    display: 'flex',
  }
}));


function EditQuestionnaire() {

  return (
    <Container maxWidth="false">
      <ButtonAppBar />
      <ReorderableQuestions />
    </Container>
  );

}

// send questions through props and use that as 
// initial state in useState(init_param) hook
const ReorderableQuestions = (props) => {

  const classes = useStyles();
  const [valuez, setValue] = React.useState('female');

  const handleChange = event => {
    setValue(event.target.value);
  };

  const [items, setItems] = useState([
    {
      id: "v1",
      type: "checkbox",
      title: 'Waar hadden de belangrijkste gebeurtenissen mee te maken?',
      options: ['hobby/sport', 'werk', 'vriendschap', 'romantische relatie', 'thuis']
    },
    {
      id: "v1",
      type: "radio",
      title: 'Waar hadden de belangrijkste gebeurtenissen mee te maken?',
      options: ['hobby/sport', 'werk', 'vriendschap', 'romantische relatie', 'thuis']
    }
  ]);

  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const dialog = (
    <Dialog
        maxWidth="md"
        fullWidth
        onClose={handleClose}
        aria-labelledby="customized-dialog-title"
        open={open}
      >
        <DialogTitle id="customized-dialog-title" onClose={handleClose}>
          Edit question
        </DialogTitle>
        <DialogContent dividers>
          {/* <DialogContentText>
            To subscribe to this website, please enter your email address here. We will send updates
            occasionally.
          </DialogContentText> */}
          <form noValidate autoComplete="off" onSubmit={handleSubmit}>
            <div>
              <TextField
                className={classes.element}
                id="type"
                select
                label="Type"
                value={type}
                onChange={handleChange}
                variant="outlined"
                margin="dense"
              >
                {questionTypes.map(option => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </TextField>
            </div>
            <FormControl fullWidth>
              <TextField
                className={classes.element}
                id="title"
                label="Title"
                variant="outlined"
                margin="dense"
                autoComplete="off"
                onChange={handleTitleChange}
              />
            </FormControl>
          </form>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose} color="secondary">
            Cancel
          </Button>
          <Button autoFocus onClick={handleSave} color="primary">
            Save
          </Button>
        </DialogActions>
      </Dialog>
  )

  return (
    <Container className="main" maxWidth="sm">
      <List
        className="drop-container-item"
        values={items}
        onChange={({ oldIndex, newIndex }) =>
          setItems(arrayMove(items, oldIndex, newIndex))
        }
        renderList={({ children, props, isDragged }) => (
          <ul
            {...props}
            style={{ padding: 0, cursor: isDragged ? "grabbing" : undefined }}
          >
            {children}
          </ul>
        )}
        renderItem={({ value, props, isDragged, isSelected }) => (
          <li
            {...props}
            style={{
              ...props.style,
              padding: "1.5em",
              // padding: "1.5em",
              margin: "0.5em 0em",
              listStyleType: "none",
              cursor: isDragged ? "grabbing" : "grab",
              textAlign: "center"
              // zIndex: isDragged || isSelected ? 1 : 0,
              // border: '2px solid #CCC',
              // boxShadow: '3px 3px #AAA',
              // color: '#333',
              // borderRadius: '5px',
              // // fontFamily: 'Arial, "Helvetica Neue", Helvetica, sans-serif',
              // // fontFamily: "'Lato', sans-serif",
              // fontFamily: "'Raleway', sans-serif",
              // backgroundColor: isDragged || isSelected ? '#EEE' : '#FFF'
            }}
          >
            <Card variant="outlined">
              <CardContent>
                <div className="con">
                  <span>
                    <Typography component="h1" variant="h6">{items.indexOf(value)}</Typography>
                    </span>
                  <span>
                    <Typography component="caption" variant="caption">{value.type}</Typography>
                    </span>
                </div>
                <h2>{value.title}</h2>
                <div>
                  <FormControl
                    component="fieldset"
                    className={classes.formControl}
                  >
                    <RadioGroup
                      aria-label="options"
                      name="options"
                      value={valuez}
                      onChange={handleChange}
                    >
                      {value.options.map(option => (
                        <FormControlLabel
                          value={option}
                          control={<Radio />}
                          label={option}
                        />
                      ))}
                    </RadioGroup>
                  </FormControl>
                </div>
              </CardContent>
              <CardActions align="end">
                <Button size="small" color="primary" onClick={handleClickOpen}>
                  Edit
                </Button>
                <Button size="small" color="secondary">
                  Remove
                </Button>
              </CardActions>
            </Card>
          </li>
        )}
      />
    </Container>
  );
};

export default EditQuestionnaire;
