import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectProperty,
  setProperty,
} from "../../features/questionProperties/questionSlice";
import { TextField, FormControl, InputLabel, FilledInput, makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  boxy: {
    borderRadius: 0,
  },
}));

export const TextProperty = ({ name, propertyName, ...props }) => {
  const property = useSelector(selectProperty(propertyName));
  const dispatch = useDispatch();

  const handleChange = (event) => {
    dispatch(
      setProperty({
        property: propertyName,
        value: event.target.value,
      })
    );
  };

  const classes = useStyles();

  return (
    // <TextField
    //   key={propertyName}
    //   fullWidth
    //   variant="filled"
    //   type="text"
    //   id={"outlined-basic"}
    //   value={property}
    //   onChange={handleChange}
    //   label={name}
    //   {...props}
    // />
    <FormControl fullWidth>
      <InputLabel variant="filled">{name}</InputLabel>
      <FilledInput
        className={classes.boxy}
        required
        autoFocus
        autoComplete="off"
        type="text"
        id={"outlined-basic"}
        placeholder={name}
        value={property || ""}
        onChange={handleChange}
        {...props}
      />
    </FormControl>
  );
};
