import React, { useState } from 'react';
import { List, arrayMove } from 'react-movable';
import { Card, CardContent } from "@material-ui/core";

const EditQuestionnaire = () => {
  const [items, setItems] = useState([
    {
      id: "v1",
      type: "checkbox",
      title: 'Waar hadden de belangrijkste gebeurtenissen mee te maken?',
      options: ['hobby/sport', 'werk', 'vriendschap', 'romantische relatie', 'thuis']
    },
    {
      id: "v1",
      type: "radio",
      title: 'Waar hadden de belangrijkste gebeurtenissen mee te maken?',
      options: ['hobby/sport', 'werk', 'vriendschap', 'romantische relatie', 'thuis']
    }
  ]);

  return (
    <div
      className="drop-container"
    >
      <List
        className="drop-container-item"
        values={items}
        onChange={({ oldIndex, newIndex }) =>
          setItems(arrayMove(items, oldIndex, newIndex))
        }
        renderList={({ children, props, isDragged }) => (
          <ul
            {...props}
            style={{ padding: 0, cursor: isDragged ? 'grabbing' : undefined }}
          >
            {children}
          </ul>
        )}
        renderItem={({ value, props, isDragged, isSelected }) => (
          <li
            {...props}
            style={{
              ...props.style,
              padding: '1.5em',
              margin: '0.5em 0em',
              listStyleType: 'none',
              cursor: isDragged ? 'grabbing' : 'grab',
              // border: '2px solid #CCC',
              // boxShadow: '3px 3px #AAA',
              // color: '#333',
              // borderRadius: '5px',
              // // fontFamily: 'Arial, "Helvetica Neue", Helvetica, sans-serif',
              // // fontFamily: "'Lato', sans-serif",
              // fontFamily: "'Raleway', sans-serif",
              // backgroundColor: isDragged || isSelected ? '#EEE' : '#FFF'
            }}
          >
            <Card>
              <CardContent>
                <h2>{value.title}</h2>
                <div>{value.options.map(option => <p>{option}</p>)}</div>
              </CardContent>
            </Card>
          </li>
        )}
      />
    </div>
  );
};

export default EditQuestionnaire;
