import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectProperty,
  setTextArrayField,
  setProperty,
} from "../../features/questionProperties/questionSlice";
import {
  TextField,
  InputAdornment,
  IconButton,
  Button,
  Box,
  Menu,
  MenuItem,
  makeStyles,
} from "@material-ui/core";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import DeleteIcon from "@material-ui/icons/Delete";

const useStyles = makeStyles(theme => ({
  boxy: {
    borderRadius: 0,
  }
}));

export const TextArrayProperty = ({ name, propertyName }) => {
  const classes = useStyles();
  const property = useSelector(selectProperty(propertyName));
  const dispatch = useDispatch();

  const handleChange = (index, event) => {
    dispatch(
      setTextArrayField({
        property: propertyName,
        index: index,
        value: event.target.value,
      })
    );
  };

  const handleAddOptionClick = () => {
    dispatch(
      setProperty({
        property: propertyName,
        value: [...property, ""],
      })
    );
  };

  const handleRemoveOptionClick = (index) => {
    dispatch(
      setProperty({
        property: propertyName,
        value: property.filter((_, i) => index !== i),
      })
    );
  };

  // MENU STUFF

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  // ----------

  const getInputProps = (index) => ({
    endAdornment: (
      <InputAdornment position="end">
        <IconButton size="small" onClick={handleMenuClick}>
          <MoreHorizIcon />
        </IconButton>
        <Menu
          id="simple-menu"
          keepMounted
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
        >
          {/* switch case without break for options */}
          <MenuItem button={false}>
            <FormControl fullWidth>
              <InputLabel variant="filled">Option tooltip</InputLabel>
              <FilledInput
                required
                className={classes.boxy}
                autoFocus
                autoComplete="off"
                type="text"
                id={`option-${index + 1}`}
                placeholder="Enter option title here.."
                value={option}
                onChange={handleChange}
                endAdornment={
                  <>
                    <MoreSettingsButton index={index} />
                    {/* <RemoveOptionButton index={index} /> */}
                  </>
                }
              />
            </FormControl>
          </MenuItem>
          <MenuItem onClick={handleMenuClose}>My account</MenuItem>
          <MenuItem onClick={handleMenuClose}>Logout</MenuItem>
        </Menu>
        <IconButton size="small" onClick={() => handleRemoveOptionClick(index)}>
          <DeleteIcon />
        </IconButton>
      </InputAdornment>
    ),
  });

  function renderElements() {
    return property.map((option, index) => (
      <TextField
        required
        key={index}
        fullWidth
        type="text"
        style={{ margin: "0.2em 0" }}
        placeholder={name}
        value={option || ""}
        onChange={(e) => handleChange(index, e)}
        InputProps={getInputProps(index)}
      />
    ));
  }

  return (
    <>
      <h4>{name}</h4>
      <Button onClick={handleAddOptionClick}>add {name}</Button>
      <Box
        fullWidth
        height="200px"
        overflow="scroll"
        style={{ margin: "0", overflowX: "hidden" }}
      >
        {renderElements()}
      </Box>
    </>
  );
};
