import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  selectProperty,
  setTextArrayField,
  setProperty,
} from "../../features/questionProperties/questionSlice";
import {
  TextField,
  InputAdornment,
  IconButton,
  Button,
  Box,
} from "@material-ui/core";
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import DeleteIcon from "@material-ui/icons/Delete";

export const TextArrayProperty = ({ name, propertyName }) => {
  const property = useSelector(selectProperty(propertyName));
  const dispatch = useDispatch();

  const handleChange = (index, event) => {
    dispatch(
      setTextArrayField({
        property: propertyName,
        index,
        value: event.target.value,
      })
    );
  };

  const handleAddOptionClick = () => {
    dispatch(
      setProperty({
        property: propertyName,
        value: [...property, ""],
      })
    );
  };

  const handleRemoveOptionClick = (index) => {
    dispatch(
      setProperty({
        property: propertyName,
        value: property.filter((_, i) => index !== i),
      })
    );
  };

  // MENU STUFF

  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleMenuClick}>
        Open Menu
      </Button>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleMenuClose}
      >
        <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
        <MenuItem onClick={handleMenuClose}>My account</MenuItem>
        <MenuItem onClick={handleMenuClose}>Logout</MenuItem>
      </Menu>
    </div>
  );

  // ----------

  const getInputProps = (index) => ({
    endAdornment: (
      <InputAdornment position="end">
        <IconButton
          size="small"  
          onClick={handleMenuClick}
        >
          <MoreHorizIcon />
        </IconButton>
        <IconButton
          size="small"
          onClick={() => handleRemoveOptionClick(index)}
        >
          <DeleteIcon />
        </IconButton>
      </InputAdornment>
    ),
  });

  function renderElements() {
    return property.map((option, index) => (
      <TextField
        required
        key={index}
        fullWidth
        type="text"
        style={{ margin: "0.2em 0" }}
        placeholder={name}
        value={option || ""}
        onChange={(e) => handleChange(index, e)}
        InputProps={getInputProps(index)}
      />
    ));
  }

  return (
    <>
      <h4>{name}</h4>
      <Button onClick={handleAddOptionClick}>add {name}</Button>
      <Box
        fullWidth
        height="200px"
        overflow="scroll"
        style={{ margin: "0", overflowX: "hidden" }}
      >
        {renderElements()}
      </Box>
    </>
  );
};
