import { createSlice } from "@reduxjs/toolkit";
import {
  initial2,
  preprocessQuestion,
  CLEAN_SUPER_QUESTION,
} from "../../utils";

export const questionSlice = createSlice({
  name: "question",
  initialState: CLEAN_SUPER_QUESTION,
  reducers: {
    setProperty: (state, action) => {
      state[action.payload.property] = action.payload.value;
    },
    setTextArrayField: (state, action) => {
      state[action.payload.property][action.payload.index] = action.payload.value;
    },
    resetAll: (state, action) => {
      return CLEAN_SUPER_QUESTION;
    },
  },
});

export const { setProperty, setTextArrayField, resetAll } = questionSlice.actions;

export function selectProperty(propertyName) {
  return (state) => state.question[propertyName];
}

export default questionSlice.reducer;
