import React, { useState } from 'react';
import { List, arrayMove } from 'react-movable';
import { Card, CardContent } from "@material-ui/core";
import { makeStyles } from '@material-ui/core/styles';
import { Container } from '@material-ui/core';
import ButtonAppBar from './ButtonAppBar';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles(theme => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));


function EditQuestionnaire() {

  return (
    <Container maxWidth="false">
      <ButtonAppBar />
      <ReorderableQuestions />
    </Container>
  );

}

const ReorderableQuestions = () => {
  const classes = useStyles();
  const [age, setAge] = React.useState('');

  const inputLabel = React.useRef(null);
  const [labelWidth, setLabelWidth] = React.useState(0);
  React.useEffect(() => {
    setLabelWidth(inputLabel.current.offsetWidth);
  }, []);

  const handleChange = event => {
    setAge(event.target.value);
  };

  const [items, setItems] = useState([
    {
      id: "v1",
      type: "checkbox",
      title: 'Waar hadden de belangrijkste gebeurtenissen mee te maken?',
      options: ['hobby/sport', 'werk', 'vriendschap', 'romantische relatie', 'thuis']
    },
    {
      id: "v1",
      type: "radio",
      title: 'Waar hadden de belangrijkste gebeurtenissen mee te maken?',
      options: ['hobby/sport', 'werk', 'vriendschap', 'romantische relatie', 'thuis']
    }
  ]);


  return (
    <Container className="main" maxWidth="md">
      <List
        className="drop-container-item"
        values={items}
        onChange={({ oldIndex, newIndex }) =>
          setItems(arrayMove(items, oldIndex, newIndex))
        }
        renderList={({ children, props, isDragged }) => (
          <ul
            {...props}
            style={{ padding: 0, cursor: isDragged ? "grabbing" : undefined }}
          >
            {children}
          </ul>
        )}
        renderItem={({ value, props, isDragged, isSelected }) => (
          <li
            {...props}
            style={{
              ...props.style,
              padding: "1.5em",
              margin: "0.5em 0em",
              listStyleType: "none",
              cursor: isDragged ? "grabbing" : "grab",
              textAlign: "center"
              // border: '2px solid #CCC',
              // boxShadow: '3px 3px #AAA',
              // color: '#333',
              // borderRadius: '5px',
              // // fontFamily: 'Arial, "Helvetica Neue", Helvetica, sans-serif',
              // // fontFamily: "'Lato', sans-serif",
              // fontFamily: "'Raleway', sans-serif",
              // backgroundColor: isDragged || isSelected ? '#EEE' : '#FFF'
            }}
          >
            <Card variant="outlined">
              <CardContent>
                <div>
                  <FormControl
                    variant="outlined"
                    className={classes.formControl}
                  >
                    <InputLabel
                      ref={inputLabel}
                      id="demo-simple-select-outlined-label"
                    >
                      Age
                    </InputLabel>
                    <Select
                      labelId="demo-simple-select-outlined-label"
                      id="demo-simple-select-outlined"
                      value={age}
                      onChange={handleChange}
                      labelWidth={labelWidth}
                    >
                      <MenuItem value=""><em>None</em></MenuItem>
                      <MenuItem value={10}>Ten</MenuItem>
                      <MenuItem value={20}>Twenty</MenuItem>
                      <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                  </FormControl>
                </div>
                <h2>{value.title}</h2>
                <div>
                  {value.options.map(option => (
                    <p>{option}</p>
                  ))}
                </div>
              </CardContent>
            </Card>
          </li>
        )}
      />
    </Container>
  );
};

export default EditQuestionnaire;
