import React, { useContext } from "react";
import { v1 as uuidv1 } from "uuid";
import {
  Container,
  Typography,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Box
} from "@material-ui/core";
import { QuestionnaireContext } from "../contexts/QuestionnaireContext";
import Question from "./Question";
import AddQuestionButton from "./buttons/AddQuestionButton";
import ToggleGridAreasButton from "./buttons/ToggleGridAreasButton";
import AddQuestionButton2 from "./buttons/AddQuestionButton2";
import StringifiedJSONCard from "./StringifiedJSONCard";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import { v4 as uuid } from "uuid";
import { useEffect } from "react";

const reorder = (list, startIndex, endIndex) => {
  const [removed] = list.splice(startIndex, 1);
  list.splice(endIndex, 0, removed);
  return list;
};

const copy = (source, destination, droppableSource, droppableDestination) => {
  const item = source[droppableSource.index];
  destination.splice(droppableDestination.index, 0, {
    id: uuid(),
    type: item.label,
    title: `untitled ${item.label}`,
    options: ["option1", "option2", "option3", "option4"]
  });
  return destination;
};

const QuestionsPage = () => {
  const { questions, dispatch } = useContext(QuestionnaireContext);
  const [shoppingBagItems, setShoppingBagItems] = React.useState(questions);
  const onDragEnd = React.useCallback(
    result => {
      const { source, destination } = result;

      if (!destination) {
        return;
      }

      switch (source.droppableId) {
        case destination.droppableId:
          setShoppingBagItems(state => {
            const newQuestions = reorder(state, source.index, destination.index);
            dispatch({ type: "SET_QUESTIONS", questions: newQuestions });
            console.log(newQuestions);
            return newQuestions;
          }
          );
          break;
        case "SHOP":
          setShoppingBagItems(state => {
            const newQuestions = copy(QUESTION_TYPES, state, source, destination);
            dispatch({ type: "SET_QUESTIONS", questions: newQuestions });
            console.log(newQuestions);
            return newQuestions;
          });
          break;
        default:
          break;
      }
    },
    [setShoppingBagItems]
  );
  useEffect(() => {
    setShoppingBagItems(questions);
  }, [questions])
  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <Toolbar items={QUESTION_TYPES} />
      <Container maxWidth="sm">
        <TopSection />
        <BottomSection items={questions} />
      </Container>
    </DragDropContext>
  );
};

export default QuestionsPage;

const TopSection = () => {
  return (
    <Container style={{ textAlign: "center", margin: "2em auto" }}>
      {/* <AddQuestionButton /> */}
      <ToggleGridAreasButton />
      <StringifiedJSONCard />
    </Container>
  );
};

const BottomSection = ({ items }) => {
  const { questions } = useContext(QuestionnaireContext);

  return (
    <Container style={{ textAlign: "center", margin: "1em auto" }}>
      <Typography variant="h4" style={{ margin: "1em 0" }}>
        Questions
      </Typography>
      <Droppable droppableId="BAG">
        {(provided, snapshot) => (
          <Box ref={provided.innerRef} className="shopping-bag">
            {questions.map((question, index) => (
              <Draggable
                key={question.id}
                draggableId={question.id}
                index={index}
              >
                {(provided, snapshot) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.draggableProps}
                    {...provided.dragHandleProps}
                    style={provided.draggableProps.style}
                  >
                    <Question key={uuidv1()} question={question} />
                  </div>
                )}
              </Draggable>
            ))}
            {provided.placeholder}
          </Box>
        )}
      </Droppable>
      <AddQuestionButton2 />
    </Container>
  );
};

// This method is needed for rendering clones of draggables
const getRenderItem = (items, className) => (provided, snapshot, rubric) => {
  const item = items[rubric.source.index];
  return (
    <React.Fragment>
      <li
        {...provided.draggableProps}
        {...provided.dragHandleProps}
        ref={provided.innerRef}
        style={provided.draggableProps.style}
        className={snapshot.isDragging ? "dragging" : ""}
      >
        {item.label}
      </li>
    </React.Fragment>
  );
};

const QUESTION_TYPES = [
  { id: uuid(), label: "checkbox" },
  { id: uuid(), label: "radio" },
  { id: uuid(), label: "likert" },
  { id: uuid(), label: "range" },
  { id: uuid(), label: "raw" },
  { id: uuid(), label: "textarea" },
  { id: uuid(), label: "textfield" },
  { id: uuid(), label: "number" },
  { id: uuid(), label: "expandable" },
  { id: uuid(), label: "time" },
  { id: uuid(), label: "date" },
  { id: uuid(), label: "unsubscribed" },
  { id: uuid(), label: "dropdown" },
  { id: uuid(), label: "drawing" }
];

const Toolbar = ({ items }) => {
  return (
    <Droppable
      renderClone={getRenderItem(items, "")}
      droppableId="SHOP"
      isDropDisabled={true}
    >
      {(provided, snapshot) => (
        <Drawer ref={provided.innerRef} variant="permanent" anchor="left">
          <List>
            {items.map((item, index) => {
              const shouldRenderClone =
                item.id === snapshot.draggingFromThisWith;
              return (
                <React.Fragment key={item.id}>
                  {shouldRenderClone ? (
                    <ListItem button key={item.label}>
                      <ListItemText primary={item.label} />
                    </ListItem>
                  ) : (
                    <Draggable draggableId={item.id} index={index}>
                      {(provided, snapshot) => (
                        <div
                          key={item.label}
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          <ListItem
                            // button
                            // className={snapshot.isDragging ? "dragging" : ""}
                          >
                            <ListItemText primary={item.label} />
                          </ListItem>
                        </div>
                      )}
                    </Draggable>
                  )}
                </React.Fragment>
              );
            })}
          </List>
        </Drawer>
      )}
    </Droppable>
  );
};
