import React, { useState } from 'react'
import Card from '@material-ui/core/Card'
import CardContent from '@material-ui/core/CardContent'
import CardActions from '@material-ui/core/CardActions'
import Typography from '@material-ui/core/Typography'
import FormControl from '@material-ui/core/FormControl'
import FormControlLabel from '@material-ui/core/FormControlLabel'
import RadioGroup from '@material-ui/core/RadioGroup'
import Radio from '@material-ui/core/Radio'
import Button from '@material-ui/core/Button'
import { FormGroup, Dialog, DialogTitle, DialogContent, DialogActions, DialogContentText, TextField } from '@material-ui/core'


const RadioEditDialog = ({value, items, setItems}) => {

  const [title, setTitle] = useState('');
  const [open, setOpen] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();

    const newItem = {...value, title: title};
    // const filtered = items.filter(item => value.id != item.id);
    // console.log(filtered);
    // const newItems = [...filtered, newItem]
    // console.log(newItems);
    const newItems = items.map(item => (item.id == newItem.id ? newItem : item))
    setItems(newItems);

    setOpen(false);
  }

  return (
    <div>
      <Button color="primary" onClick={() => setOpen(true)}>
        edit
      </Button>
      <Dialog open={open} onClose={() => setOpen(false)}>
        <form onSubmit={handleSubmit}>
          <DialogTitle>
            edit this question
          </DialogTitle>
          <DialogContent>
            <DialogContentText>
              edit this question and when done press save or cancel
            </DialogContentText>
            <TextField
              autoComplete="off"
              value={title}
              onChange={event => setTitle(event.currentTarget.value)}
              autoFocus
              variant="outlined"
              margin="dense"
              id="title"
              label="title"
              fullWidth
            />
          </DialogContent>
          <DialogActions>
            <Button color="primary" type="submit">
              save
            </Button>
            <Button color="secondary" onClick={() => setOpen(false)}>
              cancel
            </Button>
          </DialogActions>
        </form>
      </Dialog>
    </div>
  )
}


const RadioTemplate = ({ value, items, setItems }) => {
  const options = value.options.map(option =>
    <FormControlLabel
      disabled
      label={option}
      control={<Radio />}
    />
  );

  return (
    <Card variant="outlined">
      <CardContent>
        <div className="header">
          <span>
            <Typography variant="h6">
              {items.indexOf(value)}
            </Typography>
          </span>
          <span>
            <Typography variant="caption">
              {value.type}
            </Typography>
          </span>
        </div>
        <div className="title">
          <Typography variant="h5">
						{value.title}
					</Typography>
        </div>
        <div className="options">
          <FormControl>
            <FormGroup>
              {options}
            </FormGroup>
          </FormControl>
        </div>
      </CardContent>
      <CardActions>
        <RadioEditDialog value={value} items={items} setItems={setItems}/>
        <Button color="secondary">
          remove
        </Button>
      </CardActions>
    </Card>
  )
}


export default RadioTemplate;
